import { ADD_TO_BLOG } from "../Constant"

const initialState = {
    blogData : []
}

 function BlogItem(state = initialState, action)
{
      switch(action.type)
      {
                 case ADD_TO_BLOG : 

                    return {
                        ...state,
                        blogData : action.data
                    }
                    break;
                 default :
                 return state
      }
}

export default BlogItem;
