import React , { useState }  from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Link ,useLocation} from 'react-router-dom';

function MyBlog(props)
{
      let Name = window.location.href.split('?')[1].split('=')[1];
      let Email = window.location.href.split('?')[2].split('=')[1];
      const location = useLocation();
      console.log("lllll",location.state.val);

      let [CurrentBlog ,setCurrentBlog] = useState(location.state.val[0].blogData);
      let [AllBlog,setAllBlog] = useState(location.state.val);
      let [NextEnable ,setNext] = useState(true);
      let [PrevEnable ,setPrev] = useState(false);
      let [index,setIndex] = useState(0);

      function next(AllBlogData,indexval)
      {
             let AllBlog = AllBlogData.AllBlog;
             let indexNo = indexval.index;
             setCurrentBlog(AllBlog[indexNo+1].blogData);
             setIndex(prev=> prev+1);
             setPrev(true);
             if(indexNo == AllBlog.length-2)
             {
                    setNext(false);
             }
  
      }
      function prev(AllBlogData,indexval)
      {
             let AllBlog = AllBlogData.AllBlog;
             let indexNo = indexval.index;
             setCurrentBlog(AllBlog[indexNo-1].blogData);
             setIndex(prev=> prev-1);
             setNext(true);
             if(indexNo == 1)
             {
                    setNext(false);
             }
  
      }

      return (
         <>

        <div class="Blog">
        <div class ="TopNavigation d-flex flex-row">
            <a href = "/abc">Back To Home</a>
             <div style ={{marginLeft: '330px'}}>
            <Link to = '/AddNewBlog'>
             <button type="button" class="btn btn-success Button" style = {{borderRadius:'30px'}}>Add New Post</button>
             </Link>
                 <button type="button" class="btn btn-warning Button" style = {{borderRadius:'30px'}}>Edit</button>
                 <button type="button" class="btn btn-danger Button" style = {{borderRadius:'30px'}} onClick = {() => props.removeToBlogHandler(CurrentBlog)}>DeLETE</button>
            </div>
       </div>
        <div class = 'd-flex flex-column'>
         <div class='Text'>
            <span class = "TextVal">{"Title "}</span> {CurrentBlog.Title}
         </div>

         <div class='Text'>
         <span class = "TextVal">{"Categorized "}</span> {CurrentBlog.Content}
         </div>

         <div class='Text'>
         <span class = "TextVal">{"Content "}</span> {CurrentBlog.Categorized}
         </div>
         </div>

         <div class="d-flex mt-3 ml-5">

{NextEnable ? <button type="button" className="btn btn-warning" onClick={() => next({AllBlog},{index})}>Next</button>
                : <button type="button" className="btn btn-warning"disabled>Next</button>}
                {PrevEnable ? <button type="button" className="btn btn-dark ml-3" onClick={() => prev({AllBlog},{index})}>Previous</button>
                :<button type="button" className="btn btn-dark ml-3" disabled>Previous</button>}
         </div>
          
      </div>
      </>
      )
}

export default MyBlog;
