import { ADD_TO_BLOG , REMOVE_TO_BLOG } from "../Constant"

const initialState = {
    blogData : []
}

 function BlogItem(state = [], action)
{
      switch(action.type)
      {
                 case ADD_TO_BLOG : 
                    //console.log("red...",action);
                    return [
                        ...state,
                        {blogData : action.data}
                    ]
                    break;
                    case REMOVE_TO_BLOG : 
                    console.log("red...",action.data);
                    console.log(state);

                    let x = state.filter((x.blogData = (x.ID.ID != action.data.ID.ID)));

                    return [
                        ...state,
                        {blogData : action.data}
                    ]
                    break;
                 default :
                 return state
      }
      
}

export default BlogItem;
