import React, { useState }  from 'react';
import './App.css';

function AddNewBlog()
{
    let  [Title, setTitle] = useState("");
    let  [Content , setContent] = useState("");
    let  [Categorized, setCategorized] = useState("");
    function onChangeData(event)
    {
        event.Field == "Title" ? setTitle(event.event.target.value) : 
        
        (event.Field == "content" ? setContent(event.event.target.value) : setCategorized(event.event.target.value));
    }

    function submitData(data)
    {
        console.log(data);
    }


    return (
          
        <>
    <div class ="TopNavigation">
      Back To Index
    </div>
    <div id="form-main">
    <div id="form-div">
    <form class="form" id="form1">
      
      <p class="name">
        <input name="name" type="text" class="validate[required,custom[onlyLetter],length[0,100]] feedback-input" placeholder="Title" id="Title" value = {Title} onChange={(e) => onChangeData({event : e , Field : "Title"})}/>
      </p>
      
      <p class="email">
        <input name="email" type="text" class="validate[required,custom[email]] feedback-input" id="Categorized" placeholder="Categorized" value = {Categorized} onChange={(e) => onChangeData({event : e , Field : "Categorized"})} />
      </p>
      
      <p class="text">
        <textarea name="text" class="validate[required,length[6,300]] feedback-input" id="comment" placeholder="content" value = {Content} onChange={(e) => onChangeData({event : e , Field : "content"})}></textarea>
      </p>
      
      
        <button type="button" class="btn btn-warning"  onSubmit={submitData}>Submit</button>
        <button type="button" class="btn btn-secondary Secondary">Secondary</button>

    </form>
  </div>
  </div>
  </>
    );
}

export default AddNewBlog
